use movie_review;

CREATE TABLE IF NOT EXISTS movie_review.movie (
    `_id` INT NOT NULL AUTO_INCREMENT,
    `story` TEXT(1024) CHARACTER SET utf8,
    `buy` VARCHAR(255) CHARACTER SET utf8,
    `video1` VARCHAR(255) CHARACTER SET utf8,
    `thumb` VARCHAR(255) CHARACTER SET utf8,
    `video2` VARCHAR(255) CHARACTER SET utf8,
    `poster` VARCHAR(255) CHARACTER SET utf8,
    `advice` VARCHAR(255) CHARACTER SET utf8,
    `title` VARCHAR(255) CHARACTER SET utf8,
    `cast` TEXT(1024) CHARACTER SET utf8,
    `director` VARCHAR(60) CHARACTER SET utf8,
    `genre` VARCHAR(45) CHARACTER SET utf8,
    `duration` VARCHAR(30) CHARACTER SET utf8,
    `release` VARCHAR(30) CHARACTER SET utf8,
    `availability` VARCHAR(15) CHARACTER SET utf8,
PRIMARY KEY (`_id`))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4
COLLATE = utf8mb4_unicode_ci;




